mapper.select:
select place_id
from plan_place_info
where plan_id = #{plan_id}
order by sequence;

mapper.insert:
insert into plan_place_info
(plan_id, place_id, sequence)
values (#{plan_id}, #{place_id}, #{sequence});

mapper.select:
select count(place_id)
from place_info
where place_id = #{place_id};
